import Vue from 'vue'
import { createStore } from 'vuex'
export default new Vuex.Store({
    state: {
        token: null,
        acceptCookies: false,
    },
    getters: {
        getToken: state => state.token,
        areCookiesAccepted: state => state.acceptCookies
    },
    mutations: {
        setToken(state, token) {
            Vue.set(state.user, 'token', token);
        },
        setAcceptedCookies(state, bool) {
            Vue.set(state, 'acceptCookies', bool);
        }
    }
})
