import { createStore } from "vuex"
const store = createStore({
    state () {
      return {
        idUser: null,
        username: null,
        email: null,
      }
    },
    mutations: {
      setToken (state, token) {
        state.token = token
      },
      setIdUser (state, idUser) {
        state.idUser=idUser
      },
      setEmailUser
    },
    getters: {
      getToken(state) {
        return state.token
      },
      getIdUser(state) {
        return state.idUser
      },
      getUsername(state) {
        return state.username
      },
      getEmail(state) {
        return state.email
      }
    }

})
export default store
